#!/usr/bin/ens python3.4import codecsimport jsonimport csvclass Equipment:	"""docstring for Equipment"""	def __init__(self, EquName, Insnum):		self.EquName = EquName		self.Insnum = Insnum	def __repr__(self):		return "{} - {}".format(self.Insnum, self.EquName)	def sql_create(self):		return "CREATE TABLE IF NOT EXISTS Equipment (EquName text, InsNum text)"	def sql_insert(self):		valuess = (str(self.Insnum), str(self.EquName))		return "INSERT INTO Equipment VALUES (\"{1}\", \"{0}\")".format(self.Insnum, self.EquName.replace("\"",""))def parse_json_equipment(json_file):	equipment = []		json_data = codecs.open(json_file, encoding="utf-8").read()	data = json.loads(json_data)	for item in data["data"]:		equipment.append(Equipment(item["EquNom"], item["InsNumeroInstall"]))	return equipmentdef parse_csv_equipment(csv_file):        equipment = []        with open(csv_file, 'rt') as csvfile:                equipmentReader = csv.reader(csvfile, delimiter=',', quotechar='"')                for row in equipmentReader:                        equipment.append(Equipment(row[3], row[2]))                return equipment