#!/usr/bin/ens python3.4"""numero : EquipementIdnom : EquNom"""import codecsimport jsonimport csvclass Equipment:	"""docstring for Equipment"""	def __init__(self, name, numb, numb2):		self.name = name		self.numb = numb		self.numb2 = numb2	def __repr__(self):		return "{} - {}".format(self.numb, self.name, self.numb2)	def sql_create(self):		return "CREATE TABLE IF NOT EXISTS Equipment (name text, numb text, numb2 text)"	def sql_insert(self):		valuess = (str(self.numb), str(self.name))		return "INSERT INTO Equipment VALUES (\"{1}\", \"{0}\", \"{2}\")".format(self.numb, self.name.replace("\"",""), self.numb2)def parse_json_equipment(json_file):	equipment = []		json_data = codecs.open(json_file, encoding="utf-8").read()	data = json.loads(json_data)	for item in data["data"]:		equipment.append(Equipment(item["EquNom"], item["EquipementId"], item["InsNumeroInstall"]))	return equipmentdef parse_csv_equipment(csv_file):        equipment = []        with open(csv_file, 'rt') as csvfile:                equipmentReader = csv.reader(csvfile, delimiter=',', quotechar='"')                for row in equipmentReader:                        equipment.append(Equipment(row[3], row[2], row[4]))                return equipment