#!/usr/bin/ens python3.4import codecsimport jsonimport csvclass Equipement:	"""docstring for Installation"""	def __init__(self, Insnom, Insnum):		self.Insnom = Insnom		self.Insnum = Insnum	def __repr__(self):		return "{} - {}".format(self.Insnum, self.Insnom)	def sql_create(self):		return "CREATE TABLE IF NOT EXISTS equipement (numero integer, nom text)"	def sql_insert(self):		return "INSERT INTO equipement VALUES (\"{1}\", \"{0}\")".format(self.Insnum, self.Insnom)def parse_json_equipement(json_file):	equipement = []		json_data = codecs.open(json_file, encoding="utf-8").read()	data = json.loads(json_data)	for item in data["data"]:		equipement.append(Equipement(item["InsNom"], item["InsNumeroInstall"]))	return equipementdef parse_csv_equipement(csv_file):        equipement = []        with open(csv_file, 'rt') as csvfile:                equipementReader = csv.reader(csvfile, delimiter=',', quotechar='"')                for row in equipementReader:                        equipement.append(Equipement(row[3], row[2]))                return equipement